
public class Variable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        // 변수 실습 해보기
		int x = 4, y = 2; 
		System.out.println(x+y); //덧셈
		System.out.println(x-y); //뺄셈
		System.out.println(x*y); //곰셈
		System.out.println(x/y); //나눗셈
		System.out.println("x에 설정한 값은" + x + "입니다");
		System.out.println("y에 설정한 값은" + y + "입니다");
		int year = 0, age = 22 ;
		year = age + 2000 ;
		System.out.println(year);
		age = age + 1 ;
		System.out.println(age) ;
		System.out.println("나의 나이는" + age + "입니다");
		//문자 타입 char ch
		//정수 타입 byte, short, int, long
		//실수 타입 double, float
	    char ch = 'B';
	    System.out.println(ch) ;
	    System.out.println(ch + "D");
	   	    		
	}

}
